        if(rows.get(0).getProperty("location").contains(";")){
            location=rows.get(0).getProperty("location").replaceAll(";"," ");
        }
        else{
            location=rows.get(0).getProperty("location");
        }
        location=location.toLowerCase();
        if(tags.equals(""))
            tags="location: "+location;
        else
            tags+=", location: "+location;
    }
    if(!rows.get(0).getProperty("speciality").equals(""))
    {
        if(rows.get(0).getProperty("speciality").contains(";")){
