public FormRowSet store(Element element, FormRowSet rows, FormData formData) {
     String tags="";
    //check the rows is not empty before store it
    if (rows != null && !rows.isEmpty()) {
        //Get the submitted data
        FormRow row = rows.get(0);
        
        if(!"#requestParam.id#".equals("")){
            deleteOldRows();
        }
        
        String searchResult=searchAPI();
        row.setProperty("result", searchResult);
        if(searchResult.contains("nb_results")){
            Document doc= Document.parse(searchResult);
            row.setProperty("saved_companiesWs", Integer.toString(doc.getInteger("nb_results")));
        }
        tags=setTags();
        row.setProperty("tags", tags);
        // LogUtil.info("-------------------------------------------------------->Tags: "+searchResult,"");
        //Reuse Workflow Form Binder to store data
        PluginManager pluginManager = (PluginManager) AppUtil.getApplicationContext().getBean("pluginManager");
        FormStoreBinder binder = (FormStoreBinder) pluginManager.getPlugin("org.joget.apps.form.lib.WorkflowFormBinder");
        binder.store(element, rows, formData);
    }
     
    return rows;
}
 
