public String getPeople(String url){
    Connection con = null;
    String result="";
        try {
            System.out.println("in people");
            // retrieve connection from the default datasource
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            // execute SQL query
            int count=0;
            if(!con.isClosed()) {
                PreparedStatement stmt = con.prepareStatement("SELECT JSON_OBJECT('id', id, 'name', fullname, 'job', job_company, 'linkedin' , linkedin) from app_fd_people where company_url like ?;");
                stmt.setString(1, url);
                ResultSet rs = stmt.executeQuery();
           
                while (rs.next()) {
                    result+=rs.getString(1)+',';
                    count++;
                }
           
               if(result.length()>0){
                result=result.substring(0, result.length() - 1);
                result="["+result+"]";
               }
           
                //System.out.println(result);
                con.close();
            }
        } catch(Exception e) {
            LogUtil.error("Sample app - Form 1", e, "Error loading user data in load binder");
        }
       // System.out.println(result);
       System.out.println("last people");
        return result;
       
}
