public String getReview(String id_search,String id_profile){
    try{
        String jsonUrl = "#envVariable.backend_hirevioo_url_api#/getReview?id_search="+id_search+"&id_profile="+id_profile;

        CloseableHttpClient client = null;
        CloseableHttpClient client = HttpClients.createDefault();

        HttpRequestBase request = null;
        request = new HttpGet(jsonUrl);

        HttpResponse response = client.execute(request);
        HttpEntity entity = response.getEntity();
        // Read the contents of an entity and return it as a String.
        String res = EntityUtils.toString(entity,StandardCharsets.UTF_8);
        request.releaseConnection();
        client.close();
        return res;

    } catch (Exception ex) {
            System.out.println(ex);
    }
}

