public String getAppearedInSearches(String id_profile){
    Connection con = null;
    String result="";
        try {
            // retrieve connection from the default datasource
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            // execute SQL query
            if(!con.isClosed()) {
                PreparedStatement stmt = con.prepareStatement("SELECT JSON_ARRAYAGG(JSON_OBJECT('title', advs.c_title, 'score', rechinter.score)) as appearedIN FROM `app_fd_advanced_search_form` advs join `app_fd_recherche_inter` rechinter on advs.id= rechinter.id_recherche WHERE rechinter.id_profile=? and advs.c_saved='true' and advs.createdBy='#currentUser.username#' order by advs.dateCreated desc LIMIT 3");
                stmt.setString(1, id_profile);
                ResultSet rs = stmt.executeQuery();
                while (rs.next()) {
                    result=rs.getString(1);
                }

                con.close();
            }
        } catch(Exception e) {
            LogUtil.error("Sample app - Form 1", e, "Error loading user data in load binder");
        }

        if(result != null)
            return result;
        else
            return "";
}

