FormRowSet f = new FormRowSet();
f.setMultiRow(true);

//Get Joget's current datasource configs
DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");

con = ds.getConnection();

if (!con.isClosed()) {
    //Get the URL parameter
    String recordId = "#requestParam.id#";

    //Here you can query from one or multiple tables using JOIN etc
    String sql = "SELECT * FROM app_fd_talent_profile WHERE id=?";
    PreparedStatement stmt = con.prepareStatement(sql);
    stmt.setString(1, recordId);

    //Execute the SELECT SQL statement
    ResultSet rs = stmt.executeQuery();

    //Get value from columns of record(s)
    while (rs.next()) {
        FormRow r1 = new FormRow();
        r1.put("fullname", rs.getString(22));

        if(rs.getString(11)==null)
            r1.put("job_company","");
        else
            r1.put("job_company",rs.getString(11));

        //r1.put("job_company", rs.getString(11));
        if(rs.getString(20)==null)
            r1.put("location","");
        else
            r1.put("location",rs.getString(20));

        //r1.put("location", rs.getString(20));

        if(rs.getString(13)==null)
            r1.put("linkedin","");
        else
            r1.put("linkedin",rs.getString(13).replace("https://www.linkedin.com",""));

        //r1.put("linkedin",rs.getString(13).replace("https://www.linkedin.com",""));
        r1.put("profile_image","data:image/png;base64, "+rs.getString(38));
        r1.put("appeared_in",getAppearedInSearches(recordId));
        r1.put("people_reviewed",PeopleAlsoReviewed(recordId));
        if(rs.getString(24)==null)
            r1.put("email","Not assigned");
        else
            r1.put("email",rs.getString(24));
        if(rs.getString(17)==null)
            r1.put("phone","Not assigned");
        else
            r1.put("phone",rs.getString(17));

        if(rs.getString(17)==null)
            r1.put("twitter","Not assigned");
        else
            r1.put("twitter",rs.getString(17));

        if(rs.getString(17)==null)
            r1.put("facebook","Not assigned");
        else
            r1.put("facebook",rs.getString(17));

        if(!id_search.equals("")){
            r1.put("score",getScore(id_search,id));
            String review=getReview(id_search,id);
            if(review==null)
                r1.put("review_hf","");
            else
                r1.put("review_hf",review);
        }

        f.add(r1);
    }
}
con.close();
return f;
