public String getIndexes(){
    String id_search="#requestParam.search#";
    String res="";
    //Get Joget's current datasource configs
    DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");

    con = ds.getConnection();

    if (!con.isClosed()) {
        //Get the URL parameter
        String connectedUser="#currentUser.username#";

        //Here you can query from one or multiple tables using JOIN etc
        String sql = "SELECT id_profile FROM app_fd_recherche_inter WHERE id_recherche='"+id_search+"'";
        PreparedStatement stmt = con.prepareStatement(sql);

        //Execute the SELECT SQL statement
        ResultSet rs = stmt.executeQuery();

        //Get value from columns of record(s)
        while (rs.next()) {
           if(res==""){
               res=rs.getString(1);
           }else{
               res+="-"+rs.getString(1);
           }

        }
        con.close();
        return res;
    }

}

public String getReview(String id_search,String id_profile){
    try{
        String jsonUrl = "#envVariable.backend_hirevioo_url_api#/getReview?id_search="+id_search+"&id_profile="+id_profile;

        CloseableHttpClient client = null;
        CloseableHttpClient client = HttpClients.createDefault();

        HttpRequestBase request = null;
        request = new HttpGet(jsonUrl);

        HttpResponse response = client.execute(request);
        HttpEntity entity = response.getEntity();
        // Read the contents of an entity and return it as a String.
        String res = EntityUtils.toString(entity,StandardCharsets.UTF_8);
        request.releaseConnection();
        client.close();
        return res;

    } catch (Exception ex) {
            System.out.println(ex);
    }
}

