public String getStats(){
    Connection con = null;
    String result="";
        try {
            // retrieve connection from the default datasource
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            // execute SQL query
            if(!con.isClosed()) {
                PreparedStatement stmt = con.prepareStatement("SELECT JSON_OBJECT('type', type, 'name',name,'value',value) from stats");
                ResultSet rs = stmt.executeQuery();
                while (rs.next()) {
                    result+=rs.getString(1)+',';


                    }
                    result=result.substring(0,result.length()-1);
                    result='['+result+']';
                     System.out.println(result);

                con.close();
            }
        } catch(Exception e) {
            LogUtil.error("Sample app - Form 1", e, "Error loading user data in load binder");
        }

        return result;

}


public String getSavedTalents(){
    Connection con = null;
    String result="";
        try {
            // retrieve connection from the default datasource
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            // execute SQL query
            if(!con.isClosed()) {
                PreparedStatement stmt = con.prepareStatement("SELECT svt.id,svt.c_fullname,svt.c_profile_image,svt.c_location,svt.c_job_company,svt.c_title,max(advs.dateCreated) as date FROM `app_fd_saved_talents` svt join app_fd_recherche_inter rech on svt.id= rech.id_profile join app_fd_advanced_search_form advs on rech.id_recherche=advs.id WHERE rech.review is NOT null and svt.createdBy='#currentUser.username#' GROUP by svt.id order by date desc limit 5");
                ResultSet rs = stmt.executeQuery();
                while (rs.next()) {
                    Document doc = new Document();
                    doc.put("id",rs.getString(1));
                    doc.put("name",rs.getString(2));
                    doc.put("profilePic",rs.getString(3));
                    doc.put("location",rs.getString(4));
                    doc.put("job_company",rs.getString(5));
                    doc.put("title",rs.getString(6));
                    if(result.equals("")){
                        result+=doc.toJson();
                    }
                    else{
                        result+=","+doc.toJson();
                    }
                }

                con.close();
            }
        } catch(Exception e) {
            LogUtil.error("Sample app - Form 1", e, "Error loading user data in load binder");
        } 
        return "["+result+"]";
}

public String getSavedSearch(){
