                    doc.put("status",rs.getString(5));
                    if(result.equals("")){
                        result+=doc.toJson();
                    }
                    else{
                        result+=","+doc.toJson();
                    }
                }

                con.close();
            }
        } catch(Exception e) {
            LogUtil.error("Sample app - Form 1", e, "Error loading user data in load binder");
        } 

        return "["+result+"]";
}


public String getTotalSavedSearches(){
