            // retrieve connection from the default datasource
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            // execute SQL query
            if(!con.isClosed()) {
                PreparedStatement stmt = con.prepareStatement("insert into app_fd_user_profile(c_id_user,c_linkedin_url,c_status,c_firstname,c_lastname,c_email,id,c_phone,c_company,dateCreated) values (?,?,?,?,?,?,?,?,?,?);");
