                stmt.setString (2, linkedin);
                stmt.setString (3, status);
                stmt.setString (4, prenom);
                stmt.setString (5, nom);
                stmt.setString (6, email);
                stmt.setString (7,id);
                stmt.setString (8, phone);
                stmt.setString (9,company);
                stmt.setString (10,dateSave);
                
                stmt.execute();
                con.close();
            }
        } catch(Exception e) {
            LogUtil.error("Sample app - Form 1", e, "Error loading user data in load binder");
        }
}
  
