public boolean validate(Element element,FormData formData,String [] values) {
    
    if (values[0].length()> 0) {
        DirectoryManager dm = (DirectoryManager) AppUtil.getApplicationContext().getBean("directoryManager");
        try {
            //get user by Email (username=email)
            User user = dm.getUserByUsername("#currentUser.username#");
            String givenPassword= StringUtil.md5Base16(values[0]);
            if(!givenPassword.equals(user.getPassword())){
                String id = FormUtil.getElementParameterName(element);
                formData.addFormError(id, "Invalide Old Password");
                System.out.println("Invalide Old Password");
                return false;   
            }
            else{
                return true;
            }
        } catch(Exception e) {
            LogUtil.error("HV app - Form Sign in", e, "Error validating User settings");
        } 
    }else{
        return true;
    }
  
}
   
//call
return validate(element,formData,values);

