import java.util.regex.*;

public boolean validate(Element element,FormData formData,String [] values) {
    
    String fieldOldPassword = "old_password";
    Form form = FormUtil.findRootForm(element);
    Element oldPasswordElement = FormUtil.findElement(fieldOldPassword, form, formData);
    //get value of field 1
    String[] oldPasswordValue = FormUtil.getElementPropertyValues(oldPasswordElement, formData);
    
    if(oldPasswordValue[0].length()> 0){
        if(values[0].length()==0){
                String id = FormUtil.getElementParameterName(element);
                formData.addFormError(id, "Password required");
                System.out.println("Password required");
                return false;
            }
        else{
            String regex = "^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=\\S+$).{8,20}$"; 
            Pattern p = Pattern.compile(regex);
            Matcher m = p.matcher(values[0]);
            if(m.matches()){
                return true;
            }else{
                String id = FormUtil.getElementParameterName(element);
                formData.addFormError(id, "Invalid password, minimum length: 8 including at least one digit, one lowercase and one uppercase");
                System.out.println("Invalid password, minimum length: 8 including at least one digit, one lowercase and one uppercase");
                return false;
            }
        }
    }else{
        return true;
    }
    
  
}
   
//call
return validate(element,formData,values);

