public void updateUserProfile(String phone, String company,String firstname,String lastname, String id){
        Connection con = null;
     
    try {
        //Get Joget's current datasource configs
        DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");
 
        con = ds.getConnection();
         
        if(!con.isClosed()) {
            
            String insertSql = "UPDATE app_fd_user_profile SET c_phone =?,c_company=?,c_firstname=?,c_lastname=? WHERE c_id_user = ?;";
            
            PreparedStatement stmtInsert = con.prepareStatement(insertSql);
                 
            stmtInsert.setString(1, phone);
            stmtInsert.setString(2, company);
            stmtInsert.setString(3, firstname);
            stmtInsert.setString(4, lastname);
            stmtInsert.setString(5, id);

            ////Execute SQL statement
            stmtInsert.executeUpdate();
            
        }
    } catch (Exception ex) {
        LogUtil.error("Your App/Plugin Name", ex, "Error storing using jdbc");
    } 
    
    con.close();
}

