$(document).ready(function () {

    $('.menu-acc-title').click(function (j) {

        var dropDown = $(this).closest('.menu-acc-card').find('.menu-acc-panel');
        $(this).closest('.menu-acc').find('.menu-acc-panel').not(dropDown).slideUp();
        dropDown.stop(true, true).slideToggle();
        j.preventDefault();
        if ($(this).hasClass('active')) {
            $(this).removeClass('active');
            //	$(this).parent().parent().find(".nav-link").removeClass('active');
            $(this).parent().parent().find(".menu-acc-panel").removeClass('block');
            dropDown.css("display", "none !important");
        } else {
            $(this).closest('.acc').find('.menu-acc-title.active').removeClass('active');
            $(this).addClass('active');

        }


    });

});
